CREATE VIEW recipes_compat AS
SELECT 
    recipe_id as id,
    recipe_name,
    status,
    recipe_group,
    recipe_type,
    food_cost,
    CASE 
        WHEN menu_price > 0 
        THEN ROUND((food_cost / menu_price) * 100, 2)
        ELSE 0 
    END as food_cost_percentage,
    labor_cost,
    CASE 
        WHEN menu_price > 0 
        THEN ROUND((labor_cost / menu_price) * 100, 2)
        ELSE 0 
    END as labor_cost_percentage,
    menu_price,
    CASE 
        WHEN menu_price > 0 
        THEN menu_price - food_cost
        ELSE 0 
    END as gross_margin,
    food_cost + COALESCE(labor_cost, 0) as prime_cost,
    CASE 
        WHEN menu_price > 0 
        THEN ROUND(((food_cost + COALESCE(labor_cost, 0)) / menu_price) * 100, 2)
        ELSE 0 
    END as prime_cost_percentage,
    CAST(shelf_life_hours AS TEXT) as shelf_life,
    'hours' as shelf_life_uom,
    batch_yield as prep_recipe_yield,
    batch_yield_unit as prep_recipe_yield_uom,
    serving_size,
    serving_unit as serving_size_uom,
    CASE 
        WHEN serving_size > 0 
        THEN food_cost / serving_size
        ELSE 0 
    END as per_serving,
    station,
    instructions as procedure,
    last_cost_calculation as cost_modified,
    created_at as created_date,
    updated_at as updated_date
FROM recipes;
CREATE VIEW recipe_ingredients_compat AS
SELECT 
    ingredient_id as id,
    recipe_id,
    inventory_id as ingredient_id,
    ingredient_name,
    CASE 
        WHEN inventory_id IS NOT NULL THEN 'Product'
        ELSE 'PrepRecipe'
    END as ingredient_type,
    quantity,
    unit as unit_of_measure,
    total_cost as cost,
    created_at as created_date,
    quantity as canonical_quantity,
    unit as canonical_unit,
    'complete' as conversion_status
FROM recipe_ingredients;
CREATE VIEW menu_items_compat AS
SELECT 
    mi.menu_item_id as id,
    mi.item_name,
    mi.menu_category as menu_group,
    mi.description as item_description,
    mi.recipe_id,
    COALESCE(ma.price_override, mi.current_price) as menu_price,
    r.food_cost,
    CASE 
        WHEN COALESCE(ma.price_override, mi.current_price) > 0 
        THEN ROUND((r.food_cost / COALESCE(ma.price_override, mi.current_price)) * 100, 2)
        ELSE 0 
    END as food_cost_percent,
    CASE 
        WHEN COALESCE(ma.price_override, mi.current_price) > 0 
        THEN COALESCE(ma.price_override, mi.current_price) - r.food_cost
        ELSE 0 
    END as gross_profit,
    CASE WHEN mi.is_available THEN 'Active' ELSE 'Inactive' END as status,
    mi.current_price as serving_size,
    mi.created_at as created_date,
    mi.updated_at as updated_date,
    COALESCE(
        (SELECT m.menu_id FROM menu_assignments ma2 
         JOIN menus m ON ma2.menu_id = m.menu_id 
         WHERE ma2.menu_item_id = mi.menu_item_id 
         AND m.status = 'Active' 
         LIMIT 1), 
        1
    ) as version_id
FROM menu_items mi
LEFT JOIN recipes r ON mi.recipe_id = r.recipe_id
LEFT JOIN menu_assignments ma ON mi.menu_item_id = ma.menu_item_id;
CREATE VIEW unified_menu_items AS
SELECT 
    mi.menu_item_id as id,
    mi.item_name,
    mi.menu_category as menu_group,
    mi.description as item_description,
    mi.recipe_id,
    r.recipe_name,
    COALESCE(ma.price_override, mi.current_price) as menu_price,
    r.food_cost,
    CASE 
        WHEN COALESCE(ma.price_override, mi.current_price) > 0 
        THEN ROUND((r.food_cost / COALESCE(ma.price_override, mi.current_price)) * 100, 2)
        ELSE NULL 
    END as food_cost_percent,
    CASE WHEN mi.is_available THEN 'Active' ELSE 'Inactive' END as status,
    m.menu_name,
    m.menu_id,
    ma.category_section as category,
    ma.sort_order,
    ma.is_active as is_available
FROM menu_items mi
LEFT JOIN recipes r ON mi.recipe_id = r.recipe_id
LEFT JOIN menu_assignments ma ON mi.menu_item_id = ma.menu_item_id
LEFT JOIN menus m ON ma.menu_id = m.menu_id
WHERE m.menu_name IS NOT NULL;
CREATE VIEW recipe_ingredients AS
SELECT 
    ingredient_id as id,
    recipe_id,
    inventory_id as ingredient_id,
    ingredient_name,
    CASE 
        WHEN inventory_id IS NOT NULL THEN 'Product'
        ELSE 'PrepRecipe'
    END as ingredient_type,
    quantity,
    unit as unit_of_measure,
    total_cost as cost,
    created_at as created_date,
    quantity as canonical_quantity,
    unit as canonical_unit,
    'complete' as conversion_status
FROM recipe_ingredients_actual;
CREATE VIEW menus AS
SELECT 
    menu_id as id,
    menu_name,
    menu_version,
    status,
    effective_date,
    end_date,
    description,
    target_food_cost,
    created_at,
    updated_at,
    -- Add is_active and sort_order that app might expect
    CASE WHEN status = 'Active' THEN 1 ELSE 0 END as is_active,
    menu_id as sort_order
FROM menus_actual
;
CREATE VIEW menu_versions AS SELECT id, menu_name as version_name, description, CASE WHEN status = 'Active' THEN 1 ELSE 0 END as is_active, created_at as created_date, effective_date, description as notes FROM menus;
CREATE VIEW menu_items AS
SELECT 
    mi.menu_item_id as id,
    mi.item_name,
    mi.menu_category as menu_group,
    mi.description as item_description,
    mi.recipe_id,
    COALESCE(
        (SELECT ma.price_override FROM menu_assignments ma 
         WHERE ma.menu_item_id = mi.menu_item_id 
         AND ma.menu_id = (SELECT menu_id FROM menus WHERE status = 'Active' LIMIT 1)
         LIMIT 1),
        mi.current_price
    ) as menu_price,
    r.food_cost,
    CASE 
        WHEN mi.current_price > 0 
        THEN ROUND((r.food_cost / mi.current_price) * 100, 2)
        ELSE 0 
    END as food_cost_percent,
    CASE 
        WHEN mi.current_price > 0 
        THEN mi.current_price - r.food_cost
        ELSE 0 
    END as gross_profit,
    CASE WHEN mi.is_available THEN 'Active' ELSE 'Inactive' END as status,
    CAST(mi.current_price AS TEXT) as serving_size,
    mi.created_at as created_date,
    mi.updated_at as updated_date,
    COALESCE(
        (SELECT ma.menu_id 
         FROM menu_assignments ma 
         WHERE ma.menu_item_id = mi.menu_item_id 
         LIMIT 1),
        mi.version_id
    ) as version_id
FROM menu_items_actual mi
LEFT JOIN recipes_actual r ON mi.recipe_id = r.recipe_id;
CREATE VIEW menu_menu_items AS SELECT ma.assignment_id as id, ma.menu_id, ma.menu_item_id, ma.sort_order, ma.price_override, ma.is_active as is_available, ma.added_date as created_at, ma.category_section as category, ma.price_override as override_price FROM menu_assignments ma;
CREATE VIEW recipes AS
SELECT 
    recipe_id as id,
    recipe_name,
    status,
    notes,
    notes as recipe_notes,
    -- The app expects portions and portion_unit
    portions_per_batch as portions,
    serving_unit as portion_unit,
    -- The app expects target_yield
    batch_yield as target_yield,
    batch_yield_unit as units,
    food_cost,
    food_cost as prime_cost,
    labor_cost,
    ROUND((food_cost / NULLIF(menu_price, 0)) * 100, 2) as food_cost_percent,
    ROUND((food_cost / NULLIF(menu_price, 0)) * 100, 2) as food_cost_percentage,
    -- The app expects recipe_category (not in new schema, use recipe_type)
    recipe_type as recipe_category,
    recipe_type,
    recipe_group,
    created_at,
    updated_at,
    0 as version,
    -- Additional fields the app might expect
    serving_size,
    serving_unit,
    prep_time_minutes,
    cook_time_minutes,
    station,
    batch_yield,
    batch_yield_unit,
    menu_price,
    target_food_cost_percent,
    instructions,
    allergen_info,
    shelf_life_hours,
    last_cost_calculation
FROM recipes_actual
;
