CREATE VIEW vw_stg_inventory_batch_summary AS
SELECT 
    import_batch_id,
    COUNT(*) as total_rows,
    SUM(CASE WHEN needs_review = 1 THEN 1 ELSE 0 END) as needs_review_count,
    SUM(CASE WHEN review_status = 'approved' THEN 1 ELSE 0 END) as approved_count,
    SUM(CASE WHEN review_status = 'rejected' THEN 1 ELSE 0 END) as rejected_count,
    SUM(CASE WHEN is_duplicate = 1 THEN 1 ELSE 0 END) as duplicate_count,
    MIN(created_at) as batch_start,
    MAX(created_at) as batch_end
FROM stg_inventory_items
GROUP BY import_batch_id;
CREATE VIEW vw_stg_recipes_review AS
SELECT 
    staging_id,
    original_row_number,
    import_batch_id,
    needs_review,
    review_status,
    
    -- Basic info
    location_name,
    recipe_name,
    recipe_name_flag,
    status,
    recipe_group,
    recipe_type,
    recipe_type_flag,
    
    -- Costs and pricing
    food_cost,
    food_cost_flag,
    food_cost_percentage,
    food_cost_percentage_flag,
    menu_price,
    menu_price_flag,
    gross_margin,
    gross_margin_flag,
    
    -- Calculated validations
    calculated_margin,
    calculated_food_cost_percent,
    margin_variance,
    
    -- Yields and servings
    yield_quantity,
    yield_unit,
    yield_quantity_flag,
    serving_size,
    serving_size_uom,
    per_serving,
    per_serving_flag,
    
    -- Metadata
    cost_modified_date,
